import React from "react";
import "../styles/FormButtons.css";
import { useGlobalContext } from "../context";

export const FormButtons = () => {
  const {
    data,
    edit,
    handleSubmit,
    handleCancel,
    setShowForm,
    setEdit,
    info,
    setInfo,
    changedInfo,
    setChangedInfo,
  } = useGlobalContext();

  return (
    <div className="form__buttons">
      {edit ? (
        <div className="edit__buttons">
          <button
            className="btn edit"
            onClick={() => {
              setEdit(false);
              handleCancel();
            }}
          >
            <h3>Cancel</h3>
          </button>

          <button
            className="btn markpaid"
            onClick={(e) => {
              setShowForm(false);
              handleSubmit(e);
            }}
          >
            <h3>Save Changes</h3>
          </button>
        </div>
      ) : (
        <div className="submit__buttons">
          <button
            className="btn edit"
            onClick={() => {
              setEdit(false);
              handleCancel();
            }}
          >
            <h3>Discard</h3>
          </button>
          <div className="submit__buttons__se">
            <button
              className="btn edit"
              onClick={(e) => {
                setShowForm(false);
                setEdit(false);
                handleSubmit(e);
                // data[data.length].status = "draft";
              }}
            >
              <h3>Save as Draft</h3>
            </button>

            <button
              className="btn markpaid "
              onClick={(e) => {
                setShowForm(false);
                handleSubmit(e);
                // setChangedInfo({
                //   ...changedInfo,
                //   status: "pending",
                // });
              }}
            >
              <h3>Save & Send</h3>
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default FormButtons;
