import React, { useState } from "react";
import { useGlobalContext } from "../context";

function Form() {
  const { info, setInfo } = useGlobalContext();

  const [create, Setcreate] = useState(false);
  const [edit, setEdit] = useState(false);

  const {
    id,
    createdOn,
    paymentDue,
    description,
    paymentTerms,
    clientName,
    clientEmail,
    status,
    senderAddress,
    clientAddress,
    items,
    total,
  } = info;

  const handleChange = (e) => {
    const { value } = e.target;
    setInfo({ ...info, [value]: value });
  };

  return (
    <div>
      <h1>{edit ? `Edit # ${info.id}` : "New Invoice"}</h1>
      <h3>Bill From</h3>
      <form action="submit">
        <label htmlFor="senderAddressStreet">Street Address</label>
        <input
          type="text"
          value={senderAddress?.street}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="senderAddressCity">City</label>
        <input
          type="text"
          value={senderAddress?.city}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="senderAddressPostCode">Post Code</label>
        <input
          type="text"
          value={senderAddress?.postCode}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="senderAddressCountry">Country</label>
        <input
          type="text"
          value={senderAddress?.country}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <h3>Bill To</h3>
        <label htmlFor="clientName">Client's Name</label>
        <input
          type="text"
          value={clientName}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="clientEmail">Client's Email</label>
        <input
          type="email"
          value={clientEmail}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="clientAddressStreet">Street Address</label>
        <input
          type="text"
          value={clientAddress?.street}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="clientAddressCity">City</label>
        <input
          type="text"
          value={clientAddress?.city}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="clientAddressPostCode">Post Code</label>
        <input
          type="text"
          value={clientAddress?.postCode}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="clientAddressCountry">Country</label>
        <input
          type="text"
          value={clientAddress?.country}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="paymentDue">Invoice Date</label>
        <input
          type="date"
          value={paymentDue}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="paymentTerms">Payment Terms</label>
        <select
          className=""
          name="paymentTerms"
          value={paymentTerms}
          onChange={(e) => {
            handleChange(e);
          }}
        >
          <option value="1">Net 1 Day</option>;
          <option value="7">Net 1 Day</option>;
          <option value="14">Net 1 Day</option>;
          <option value="30">Net 1 Day</option>;
        </select>
        <label htmlFor="description">Project Description</label>
        <input
          type="text"
          value={description}
          onChange={(e) => {
            handleChange(e);
          }}
        />
      </form>
    </div>
  );
}

export default Form;
