import React from "react";
import { useGlobalContext } from "../context";

const Item = ({ el }) => {
  const { handleChange } = useGlobalContext();
  return (
    <div classname="grid__items">
      
      <label htmlFor="items">Item Name</label>
      <input
        type="text"
        id="items"
        name="name"
        value={el.name}
        onChange={(e) => {
          handleChange(e);
        }}
      />
      <label htmlFor="itemsQuantity">Qty.</label>
      <input
        type="text"
        id="items"
        name="quantity"
        value={el.quantity || 1}
        onChange={(e) => {
          handleChange(e);
        }}
      />
      <label htmlFor="itemsPrice">Item Price</label>
      <input
        type="text"
        id="items"
        name="price"
        value={el.price}
        onChange={(e) => {
          handleChange(e);
        }}
      />
      <h3>Total</h3>
      <h3>${(el.total = el.quantity * el.price || "0.00")}</h3>
    </div>
  );
};

export default Item;
