import React from "react";
import { useGlobalContext } from "../context";
import iconPlus from "../assets/icon-plus.svg";
import arrowDown from "../assets/icon-arrow-down.svg";

function Filter() {
  const { showFilter, setShowFilter, handleChange, data } = useGlobalContext();

  return (
    <div className="flex-horizontal width home__top">
      <div>
        <h1>Invoices</h1>
        <p>{data.length} invoices</p>
      </div>

      <div className="flex-horizontal">
        <div
          className="flex-horizontal home__top__filter"
          onClick={() => {
            setShowFilter(!showFilter);
          }}
        >
          <p>Filter</p>
          <img
            src={arrowDown}
            alt=""
            className={`${!showFilter ? "arrowDown" : "arrowDown turnAround"}`}
          />
        </div>
        <div className={`${!showFilter ? "invisible" : "filter"}`}>
          <div className="flex-horizontal">
            <input
              type="checkbox"
              name="Draft"
              value="draft"
              onChange={(e) => {
                handleChange(e);
              }}
            />
            <label htmlFor="Draft"> Draft</label>
          </div>
          <div className="flex-horizontal">
            <input
              type="checkbox"
              name="Pending"
              value="pending"
              onChange={(e) => {
                handleChange(e);
              }}
            />
            <label htmlFor="checkbox"> Pending</label>
          </div>

          <div className="flex-horizontal">
            <input
              type="checkbox"
              name="Paid"
              value="paid"
              onChange={(e) => {
                handleChange(e);
              }}
            />
            <label htmlFor="Paid"> Paid</label>
          </div>
        </div>
        <button className="home__top__filter__btn">
          <img
            src={iconPlus}
            alt=""
            className="home__top__filter__btn__iconplus"
            onClick={() => {
              setShowForm(true);
            }}
          />
          <h3>New</h3>
        </button>
      </div>
    </div>
  );
}

export default Filter;
