import React, { useState, useEffect, useRef } from "react";
import ReactDom from "react-dom";
import { useGlobalContext } from "../context";
import "../styles/Form.css";


function Form() {
  const {
    data,
    setData,
    info,
    setInfo,
    idx,
    showForm,
    setShowForm,
    changedInfo,
    setChangedInfo,
    edit,
    setEdit,
    infoFormat,
  } = useGlobalContext();

  const [filled, setFilled] = useState(false);
  const [item, setItem] = useState([]);
  console.log(info);
  console.log(changedInfo);

  let {
    id,
    createdOn,
    paymentDue,
    description,
    paymentTerms,
    clientName,
    clientEmail,
    status,
    senderAddress,
    clientAddress,
    items,
    total,
  } = changedInfo;

  const handleCancel = () => {
    setEdit(false);
    setChangedInfo({ ...info });
    setShowForm(false);
  };

  const checkForEmpty = (obj) => {
    for (const key in obj) {
    }
  };

  const idGen = function () {
    return Math.random().toString(36).substr(2, 6).toUpperCase();
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!filled) {
      console.log("sddsa");
    }
    setInfo({ ...changedInfo });

    if (!edit) {
      data.push({ ...changedInfo, id: idGen() });
      setChangedInfo({ ...infoFormat });
      console.log(data);
    } else {
      data.splice(idx, 1, changedInfo);
    }
  };

  const handleChange = (e) => {
    const { name, value, id } = e.target;

    if (!value.trim()) {
      setFilled(false);
      e.target.classList.add("empty");
    } else {
      setFilled(true);
    }

    if (id) {
      setChangedInfo({
        ...changedInfo,
        [id]: { ...changedInfo[id], [name]: value },
      });
    } else {
      setChangedInfo({ ...changedInfo, [name]: value });
    }

    console.log(filled);
  };

  if (!showForm) {
    return null;
  }

  return ReactDom.createPortal(
    <div
      className="darken-bg"
      onClick={() => {
        handleCancel();
      }}
    >
      <div
        className="form"
        onClick={(e) => {
          e.stopPropagation();
        }}
      >
        {edit ? (
          <h1>
            Edit <span className="hashtag">#</span>{id}
          </h1>
        ) : (
          <h1>New Invoice</h1>
        )}

        <h3>Bill From</h3>
        <form>
          <label htmlFor="senderAddressStreet">Street Address</label>
          <input
            type="text"
            id="senderAddress"
            name="street"
            value={senderAddress.street}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="senderAddressCity">City</label>
          <input
            type="text"
            id="senderAddress"
            name="city"
            value={senderAddress.city}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="senderAddressPostCode">Post Code</label>
          <input
            type="text"
            id="senderAddress"
            name="postCode"
            value={senderAddress.postCode}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="senderAddressCountry">Country</label>
          <input
            type="text"
            id="senderAddress"
            name="country"
            value={senderAddress.country}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <h3>Bill To</h3>
          <label htmlFor="clientName">Client's Name</label>
          <input
            type="text"
            name="clientName"
            value={clientName}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="clientEmail">Client's Email</label>
          <input
            type="email"
            name="clientEmail"
            value={clientEmail}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="clientAddressStreet">Street Address</label>
          <input
            type="text"
            id="clientAddress"
            name="street"
            value={clientAddress.street}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="clientAddressCity">City</label>
          <input
            type="text"
            id="clientAddress"
            name="city"
            value={clientAddress.city}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="clientAddressPostCode">Post Code</label>
          <input
            type="text"
            id="clientAddress"
            name="postCode"
            value={clientAddress.postCode}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="clientAddressCountry">Country</label>
          <input
            type="text"
            id="clientAddress"
            name="country"
            value={clientAddress.country}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="paymentDue">Invoice Date</label>
          <input
            type="date"
            name="paymentDue"
            value={paymentDue}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="paymentTerms">Payment Terms</label>
          <select
            className=""
            name="paymentTerms"
            value={paymentTerms}
            onChange={(e) => {
              handleChange(e);
            }}
          >
            <option value="1">Net 1 Day</option>;
            <option value="7">Net 7 Day</option>;
            <option value="14">Net 14 Day</option>;
            <option value="30">Net 30 Day</option>;
          </select>
          <label htmlFor="description">Project Description</label>
          <input
            type="text"
            name="description"
            value={description}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          {item.map((el) => {
            return (
              <div>
                {el}
                <label htmlFor="items">Item Name</label>
                <input
                  type="text"
                  id="items"
                  name="name"
                  value={items.name}
