import React, { useState, useEffect, useRef } from "react";
import ReactDom from "react-dom";
import { useGlobalContext } from "../context";
import "../styles/Form.css";
import json from "../data.json";

function Form() {
  const {
    data,
    setData,
    info,
    setInfo,
    idx,
    showForm,
    setShowForm,
    changedInfo,
    setChangedInfo,
  } = useGlobalContext();

  const [filled, setFilled] = useState(false);
  // const [changedInfo, setChangedInfo] = useState({});
  const [create, Setcreate] = useState(false);
  const [edit, setEdit] = useState(false);

  // const prevValue = useRef();
  // useEffect(async () => {
  //   await setChangedInfo({ ...info });
  // }, []);
  console.log(info);
  console.log(changedInfo);

  //   useEffect(() => {
  //     const chosenId = "RT3080";
  //     window.scrollTo(0, 0);
  //     json.forEach((el) => {
  //       if (el.id === chosenId) {
  //         setInfo({ ...el });
  //       }
  //     });
  //   }, []);

  const {
    id,
    createdOn,
    paymentDue,
    description,
    paymentTerms,
    clientName,
    clientEmail,
    status,
    senderAddress,
    clientAddress,
    items,
    total,
  } = changedInfo;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!filled) {
      console.log("sddsa");
    }
    setInfo({ ...changedInfo });
    data.splice(idx, 1, info);
setData()
    console.log(data);
  };

  const handleChange = (e) => {
    const { name, value, id } = e.target;

    if (!value.trim()) {
      setFilled(false);
      e.target.classList.add("empty");
    } else {
      setFilled(true);
    }

    if (id) {
      setChangedInfo({ ...changedInfo, [id]: { ...info[id], [name]: value } });
    } else {
      setChangedInfo({ ...changedInfo, [name]: value });
    }

    console.log(changedInfo);
    console.log(filled);
  };

  if (!showForm) {
    return null;
  }

  return ReactDom.createPortal(
    <div className="darken-bg">
      <div className="form">
        <h1>{edit ? `Edit #${changedInfo.id}` : "New Invoice"}</h1>
        <h3>Bill From</h3>
        <form
        // action="submit"
        // onSubmit={(e) => {
        //   handleSubmit(e);
        // }}
        >
          <label htmlFor="senderAddressStreet">Street Address</label>
          <input
            type="text"
            id="senderAddress"
            name="street"
            value={senderAddress.street}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="senderAddressCity">City</label>
          <input
            type="text"
            id="senderAddress"
            name="city"
            value={senderAddress.city}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="senderAddressPostCode">Post Code</label>
          <input
            type="text"
            id="senderAddress"
            name="postCode"
            value={senderAddress.postCode}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="senderAddressCountry">Country</label>
          <input
            type="text"
            id="senderAddress"
            name="country"
            value={senderAddress.country}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <h3>Bill To</h3>
          <label htmlFor="clientName">Client's Name</label>
          <input
            type="text"
            name="clientName"
            value={clientName}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="clientEmail">Client's Email</label>
          <input
            type="email"
            name="clientEmail"
            value={clientEmail}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="clientAddressStreet">Street Address</label>
          <input
            type="text"
            id="clientAddress"
            name="street"
            value={clientAddress.street}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="clientAddressCity">City</label>
          <input
            type="text"
            id="clientAddress"
            name="city"
            value={clientAddress.city}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="clientAddressPostCode">Post Code</label>
          <input
            type="text"
            id="clientAddress"
            name="postCode"
            value={clientAddress.postCode}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="clientAddressCountry">Country</label>
          <input
            type="text"
            id="clientAddress"
            name="country"
            value={clientAddress.country}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="paymentDue">Invoice Date</label>
          <input
            type="date"
            name="paymentDue"
            value={paymentDue}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <label htmlFor="paymentTerms">Payment Terms</label>
          <select
            className=""
            name="paymentTerms"
            value={paymentTerms}
            onChange={(e) => {
              handleChange(e);
            }}
          >
            <option value="1">Net 1 Day</option>;
            <option value="7">Net 7 Day</option>;
            <option value="14">Net 14 Day</option>;
            <option value="30">Net 30 Day</option>;
          </select>
          <label htmlFor="description">Project Description</label>
          <input
            type="text"
            name="description"
            value={description}
            onChange={(e) => {
              handleChange(e);
            }}
          />
        </form>
        <div className="form__buttons">
          <button
            className="top__component__btn"
            onClick={() => {
              setChangedInfo({ ...info });
              setShowForm(false);
            }}
          >
            Discard
          </button>
          <button
            className="top__component__btn"
            onClick={(e) => {
              setShowForm(false);
              handleSubmit(e);
            }}
          >
            Submit
          </button>
        </div>
      </div>
    </div>,
    document.getElementById("portal")
  );
}

export default Form;
