import React from "react";

function InvoiceButtons() {
 
 
    return (
    <>
      <button
        className={`${
          status === "paid" ? "invisible" : "top__component__btn edit"
        }`}
        onClick={() => {
          setEdit(true);
          setShowForm(true);
        }}
      >
        <h3>Edit</h3>
      </button>
      <button
        className="delete top__component__btn"
        onClick={() => {
          setConfDelete(true);
        }}
      >
        <h3>Delete</h3>
      </button>
      <button
        className={`${
          status === "paid"
            ? "invisible"
            : status === "draft"
            ? "invisible"
            : "top__component__btn markpaid"
        }`}
        onClick={() => {
          data[idx].status = "paid";
          setInfo({ ...data[idx] });
        }}
      >
        <h3>Mark as Paid</h3>
      </button>
    </>
  );
}

export default InvoiceButtons;
