import React, { useState, useEffect } from "react";
import ReactDom from "react-dom";
import { useForm } from "react-hook-form";
import { useGlobalContext } from "../context";
import "../styles/Form.css";
import Itemmm from "./Itemmm";

export default function Formmm() {
  const {
    data,
    idx,
    setIdx,
    info,
    setInfo,
    edit,
    showForm,
  } = useGlobalContext();
  
  const { register, handleSubmit, errors } = useForm({
    defaultValues: data[0],
  });
  console.log(data[idx]);

  
  const onSubmit = (dat) => {
    // setInfo({ ...info, data });
    // console.log(info);
    data.splice(idx, 1, dat);
  };
  console.log(errors);

//   if (!showForm) {
//     return null;
//   }
  return ReactDom.createPortal(
    <div
      className="darken-bg"
      //   onClick={() => {
      //     handleCancel();
      //   }}
    >
      <div
        className="form"
        onClick={(e) => {
          e.stopPropagation();
        }}
      >
        <div className="form__header">
          {edit ? (
            <h1>
              Edit <span className="hashtag">#</span>
              {info.id}
            </h1>
          ) : (
            <h1>New Invoice</h1>
          )}
        </div>
        <form onSubmit={handleSubmit(onSubmit)}>
          <h3 className="bill">Bill From</h3>
          <div className="sender">
            <div className="input">
              <label htmlFor="senderAddressStreet">Street Address</label>
              <input
                //   className={errors.senderAddress["street"] ? "error" : ""}
                type="text"
                placeholder="senderAddress.street"
                name="senderAddress.street"
                ref={register({
                  required: {
                    value: true,
                    message: "You must enter your name",
                  },
                  maxLength: 80,
                })}
              />
              {/* {errors.senderAddress["street"] && (
              <div className="error">{errors.senderAddress.street.message}</div>
            )} */}
            </div>
            <div className="sender__horizontal grid__horizontal">
              <div className="input">
                <label htmlFor="senderAddressCity">City</label>
                <input
                  type="text"
                  placeholder="senderAddress.city"
                  name="senderAddress.city"
                  ref={register({ required: true, maxLength: 80 })}
                />
              </div>
              <div className="input">
                <label htmlFor="senderAddressPostCode">Post Code</label>
                <input
                  type="text"
                  placeholder="senderAddress.postCode"
                  name="senderAddress.postCode"
                  ref={register({ required: true, maxLength: 80 })}
                />
              </div>
              <div className="input grid__third">
                <label htmlFor="senderAddressCountry">Country</label>
                <input
                  type="text"
                  placeholder="senderAddress.country"
                  name="senderAddress.country"
                  ref={register({ required: true, maxLength: 80 })}
                />
              </div>
            </div>
          </div>

          <h3 className="bill">Bill To</h3>
          <div className="input">
            <label htmlFor="clientName">Client's Name</label>
            <input
              type="text"
              placeholder="clientName"
              name="clientName"
              ref={register({ required: true, maxLength: 80 })}
            />
          </div>
          <div className="input">
            <label htmlFor="clientEmail">Client's Email</label>
            <input
              type="email"
              placeholder="clientEmail"
              name="clientEmail"
              ref={register({ required: true, maxLength: 80 })}
            />
          </div>
          <div className="input">
            <label htmlFor="clientAddressStreet">Street Address</label>
            <input
              type="text"
              placeholder="clientAddress.street"
              name="clientAddress.street"
              ref={register({ required: true, maxLength: 80 })}
            />
          </div>
          <div className="client__horizontal grid__horizontal">
            <div className="input">
              <label htmlFor="clientAddressCity">City</label>
              <input
                type="text"
                placeholder="clientAddress.city"
                name="clientAddress.city"
                ref={register({ required: true, maxLength: 80 })}
              />
            </div>
            <div className="input">
              <label htmlFor="clientAddressPostCode">Post Code</label>
              <input
                type="text"
                placeholder="clientAddress.postCode"
                name="clientAddress.postCode"
                ref={register({ required: true, maxLength: 80 })}
              />
            </div>
            <div className="input  grid__third">
              <label htmlFor="clientAddressCountry">Country</label>
              <input
                type="text"
                placeholder="clientAddress.country"
                name="clientAddress.country"
                ref={register({ required: true, maxLength: 80 })}
              />
            </div>
          </div>
          <div className="client__date grid__horizontal">
            <div className="input">
              <label htmlFor="createdOn">Invoice Date</label>
              <input
                type="date"
                placeholder="createdOn"
                name="createdOn"
                ref={register({ required: true })}
              />
            </div>
            <div className="input">
              <label htmlFor="paymentTerms">Payment Terms</label>
              <select name="paymentTerms" ref={register({ required: true })}>
                <option value="1">Net 1 Day</option>
                <option value="7">Net7 Days</option>
                <option value="14">Net 14 Days</option>
                <option value="30">Net 30 Days</option>
              </select>
            </div>
          </div>
          <div className="input">
            <label htmlFor="description">Project Description</label>
            <input
              type="text"
              placeholder="description"
              name="description"
              ref={register({ required: true, maxLength: 80 })}
            />
          </div>
          <Itemmm
            onSubmit={onSubmit}
            register={register}
            handleSubmit={handleSubmit}
            info={data[0].items}
          />

          <button
            onClick={() => {
              handleSubmit(onSubmit);
            }}
          >
            Click
          </button>
        </form>
      </div>
    </div>,
    document.getElementById("portal")
  );
}
