import React, { useState, useEffect } from "react";
import { useGlobalContext } from "./context";
import { useContext } from "react";
import json from "./data.json";

function Invoice() {
  const { chosenId } = useGlobalContext();
  const [info, setInfo] = useState({});

  console.log(chosenId);
  useEffect(() => {
    json.forEach((el) => {
      if (el.id === chosenId) {
        setInfo({ ...el });
      }
    });
  }, []);
  const {
    id,
    createdOn,
    paymentDue,
    description,
    paymentTerms,
    clientName,
    clientEmail,
    status,
    senderAddress,
    clientAddress,
    items,
    total,
  } = info;

  console.log(info);
  return (
    <div className="invoice">
      <div className="top__component">
        <span>Status {status}</span>
        <button>Delete</button>
      </div>
      <div className="bottom__component">
        <section className="top">
          <div className="id-description"></div>
          <div className="address"></div>
        </section>
        <section className="bottom">
          <div className="dates">
            <div>
              <p>Invoice Date</p>
              <h3>{createdOn}</h3>
            </div>
            <div>
              <p>Payment Due</p>
              <h3>{paymentDue}</h3>
            </div>
          </div>

          
        </section>
      </div>
    </div>
  );
}

export default Invoice;
