import React from "react";
import { useForm } from "react-hook-form";

export default function Formmm() {
  const { register, handleSubmit, errors } = useForm();
  const onSubmit = (data) => console.log(data);
  console.log(errors);

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <input
        type="text"
        placeholder="senderAddress.street"
        name="senderAddress.street"
        ref={register({ required: true, maxLength: 80 })}
      />
      <input
        type="text"
        placeholder="senderAddress.city"
        name="senderAddress.city"
        ref={register({ required: true, maxLength: 100 })}
      />

      <input type="submit" />

      <h3 className="bill">Bill From</h3>
      <div className="sender">
        <div className="input">
          <label htmlFor="senderAddressStreet">Street Address</label>
          <input
            type="text"
            id="senderAddress"
            name="street"
            value={senderAddress.street}
            onChange={(e) => {
              handleChange(e);
            }}
          />
        </div>
        <div className="sender__horizontal grid__horizontal">
          <div className="input">
            <label htmlFor="senderAddressCity">City</label>
            <input
              type="text"
              placeholder="senderAddress.city"
              name="senderAddress.city"
              ref={register({ required: true, maxLength: 80 })}
            />
          </div>
          <div className="input">
            <label htmlFor="senderAddressPostCode">Post Code</label>
            <input
              type="text"
              placeholder="senderAddress.postCode"
              name="senderAddress.postCode"
              ref={register({ required: true, maxLength: 80 })}
            />
          </div>
          <div className="input grid__third">
            <label htmlFor="senderAddressCountry">Country</label>
            <input
              type="text"
              id="senderAddress"
              name="country"
              value={senderAddress.country}
              onChange={(e) => {
                handleChange(e);
              }}
            />
          </div>
        </div>
      </div>

      <h3 className="bill">Bill To</h3>
      <div className="input">
        <label htmlFor="clientName">Client's Name</label>
        <input
          type="text"
          name="clientName"
          value={clientName}
          onChange={(e) => {
            handleChange(e);
          }}
        />
      </div>
      <div className="input">
        <label htmlFor="clientEmail">Client's Email</label>
        <input
          type="email"
          name="clientEmail"
          value={clientEmail}
          onChange={(e) => {
            handleChange(e);
          }}
        />
      </div>
      <div className="input">
        <label htmlFor="clientAddressStreet">Street Address</label>
        <input
          type="text"
          id="clientAddress"
          name="street"
          value={clientAddress.street}
          onChange={(e) => {
            handleChange(e);
          }}
        />
      </div>
      <div className="client__horizontal grid__horizontal">
        <div className="input">
          <label htmlFor="clientAddressCity">City</label>
          <input
            type="text"
            id="clientAddress"
            name="city"
            value={clientAddress.city}
            onChange={(e) => {
              handleChange(e);
            }}
          />
        </div>
        <div className="input">
          <label htmlFor="clientAddressPostCode">Post Code</label>
          <input
            type="text"
            id="clientAddress"
            name="postCode"
            value={clientAddress.postCode}
            onChange={(e) => {
              handleChange(e);
            }}
          />
        </div>
        <div className="input  grid__third">
          <label htmlFor="clientAddressCountry">Country</label>
          <input
            type="text"
            id="clientAddress"
            name="country"
            value={clientAddress.country}
            onChange={(e) => {
              handleChange(e);
            }}
          />
        </div>
      </div>
      <div className="client__date grid__horizontal">
        <div className="input">
          <label htmlFor="createdOn">Invoice Date</label>
          <input
            type="date"
            name="createdOn"
            value={createdOn}
            onChange={(e) => {
              handleChange(e);
            }}
          />
        </div>
        <div className="input">
          <label htmlFor="paymentTerms">Payment Terms</label>
          <select
            className=""
            name="paymentTerms"
            value={paymentTerms}
            onChange={(e) => {
              var result = new Date(createdOn);
              result.setDate(result.getDate() + parseInt(paymentTerms));
              console.log(result);
              setChangedInfo({ ...changedInfo, paymentDue: result });
              handleChange(e);
            }}
          >
            <option value="1">Net 1 Day</option>;
            <option value="7">Net 7 Day</option>;
            <option value="14">Net 14 Day</option>;
            <option value="30">Net 30 Day</option>;
          </select>
        </div>
      </div>
      <div className="input">
        <label htmlFor="description">Project Description</label>
        <input
          type="text"
          name="description"
          value={description}
          onChange={(e) => {
            handleChange(e);
          }}
        />
      </div>
    </form>
  );
}
