import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";

const Itemmm = ({ register, info, watch }) => {
  const [item, setItem] = useState([...info.items]);
  const [counter, setCounter] = useState();

  const addItem = () => {
    setItem((prevItem) => [...prevItem, counter]);
    setCounter((prevCounter) => prevCounter + 1);
  };

  const removeItem = (index) => () => {
    setItem((prevItem) => [...prevItem.filter((el, i) => i !== index)]);
    setCounter((prevCounter) => prevCounter - 1);
  };

  return (
    <div>
      {/* <form onSubmit={handleSubmit(onSubmit)}> */}
      {item.map((el, i) => {
        const fieldName = `items[${i}]`;
        const quantity = watch(`${fieldName}.quantity`);
        const price = watch(`${fieldName}.price`);
        // info.items[i].total = quantity * price;
        // const total = [];
        // total.concat(info.items[i].total);
        // console.log(total);
        return (
          <div name={fieldName} key={fieldName}>
            <label>
              Item Name
              <input type="text" name={`${fieldName}.name`} ref={register} />
            </label>

            <label>
              Item Qty
              <input
                type="number"
                name={`${fieldName}.quantity`}
                ref={register}
              />
            </label>

            <label>
              Item Price
              <input type="number" name={`${fieldName}.price`} ref={register} />
            </label>
            <div>
              <h3>Total</h3>
              <h3>{quantity * price || "0.00"}</h3>
            </div>
            <button
              type="button"
              onClick={
                () => {
                console.log(item.length);
                removeItem(i);
                item.length > 1 ? removeItem(i) : alert("F");
              }
            }
            >
              Remove
            </button>
          </div>
        );
      })}
      <button type="button" onClick={addItem}>
        Add Item
      </button>
    </div>
  );
};

export default Itemmm;
