import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";

const Itemmm = ({ onSubmit, register, handleSubmit, info }) => {
  const [indexes, setIndexes] = useState([...info]);
  const [counter, setCounter] = useState(info.length);

  
  const addItem = () => {
    setIndexes((prevIndexes) => [...prevIndexes, counter]);
    setCounter((prevCounter) => prevCounter + 1);
  };

  const removeItem = (index) => () => {
    setIndexes((prevIndexes) => [
      ...prevIndexes.filter((item) => item !== index),
    ]);
    setCounter((prevCounter) => prevCounter - 1);
  };

  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        {indexes.map((index) => {
          const fieldName = `items[${index}]`;
          return (
            <fieldset name={fieldName} key={fieldName}>
              <label>
                Item Name
                <input type="text" name={`${fieldName}.name`} ref={register} />
              </label>
              <label>
                Item Qty
                <input
                  type="text"
                  name={`${fieldName}.quantity`}
                  ref={register}
                />
              </label>

              <label>
                Item Price
                <input type="text" name={`${fieldName}.price`} ref={register} />
              </label>

              <div>
                <h3>Total</h3>
              </div>

              <button type="button" onClick={removeItem(index)}>
                Remove
              </button>
            </fieldset>
          );
        })}
        <button type="button" onClick={addItem}>
          Add Item
        </button>
      </form>
    </div>
  );
};

export default Itemmm;
