import React from "react";
import "../styles/Delete.css";
import { useGlobalContext } from "../context";

export default function Delete() {
  const { data,idx, info, confDelete, setConfDelete } = useGlobalContext();

  if (!confDelete) {
    return null;
  }
  return (
    <div className="delete__component">
      <h1>Confirm Deletion</h1>
      <h3>
        {`Are you sure you want to delete invoice #${info.id}? This action cannot be
        undone.`}
      </h3>
      <div className="delete__component__btns">
        <button
          className="edit top__component__btns"
          onClick={() => {
            setConfDelete(false);
          }}
        >
          Cancel
        </button>
        <button
          className="delete"
          onClick={() => {
            data.splice(idx, 1);
          }}
        >
          Delete
        </button>
      </div>
    </div>
  );
}
