import React, { useState } from "react";
import { useGlobalContext } from "./context";
import data from "./data.json";

const Filter = () => {
  const { dataJson, setDataJson } = useGlobalContext();

  const [statuses, setStatuses] = useState({
    draft: true,
    pending: true,
    paid: true,
  });

  const filter = () => {
    for (const status in statuses) {
      if (statuses[status] == false) {
        setDataJson(
          daataJson.map((el) => {
            if (el.status !== `${[status]}`) {
              console.log(el);
              return el;
            }
          })
        );
      }
    }
  };

  const handleChange = (e) => {
    setStatuses({
      ...statuses,
      [e.target.value]: !statuses[e.target.value],
    });
    console.log(statuses);
  };

  return (
    <div className="filter">
      <input
        type="checkbox"
        name="Draft"
        value="draft"
        onChange={(e) => {
          handleChange(e);
          filter();
        }}
      />
      <label htmlFor="Draft"> Draft</label>
      <input
        type="checkbox"
        name="Pending"
        value="pending"
        onChange={(e) => {
          handleChange(e);
          filter();
        }}
      />
      <label htmlFor="checkbox"> Pending</label>
      <input
        type="checkbox"
        name="Paid"
        value="paid"
        onChange={(e) => {
          handleChange(e);
        }}
      />
      <label htmlFor="Paid"> Paid</label>
    </div>
  );
};

export default Filter;
