import React, { useState } from "react";
import { useGlobalContext } from "./context";

const Filter = () => {
  const { data } = useGlobalContext();

  const [statuses, setStatuses] = useState({
    draft: true,
    pending: true,
    paid: true,
  });

  const filter = () => {
    for (status in statuses) {
      if (status == false) {
        data.map((el) => {
          if (el.status !== `${[status]}`) {
            return el;
          }
        });
      }
    }
  };

  return (
    <div className="filter">
      <input
        type="checkbox"
        name="Draft"
        value="draft"
        onChange={() => {
          setStatuses({ ...statuses, draft: false });
          filter();
        }}
      />
      <label htmlFor="Draft"> Draft</label>
      <input
        type="checkbox"
        name="Pending"
        value="pending"
        onChange={() => {
          setStatuses({ ...statuses, pending: false });
        }}
      />
      <label htmlFor="checkbox"> Pending</label>
      <input
        type="checkbox"
        name="Paid"
        value="paid"
        onChange={() => {
          setStatuses({ ...statuses, paid: false });
        }}
      />
      <label htmlFor="Paid"> Paid</label>
    </div>
  );
};

export default Filter;
