import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";

const Itemmm = ({ onSubmit, register, handleSubmit, info, watch }) => {
  const [item, setItem] = useState([...info]);
  const [counter, setCounter] = useState();

  const addItem = () => {
    setItem((prevItem) => [...prevItem, counter]);
    setCounter((prevCounter) => prevCounter + 1);
  };

  const removeItem = (index) => () => {
    setItem((prevItem) => [...prevItem.filter((el, i) => i !== index)]);
    setCounter((prevCounter) => prevCounter - 1);
  };

  // setTotal((total) => {

  //   total = multipleValues[0] * multipleValues[1];
  // });

  return (
    <div>
      {/* <form onSubmit={handleSubmit(onSubmit)}> */}
      {item.map((el, i) => {
        const fieldName = `items[${i}]`;
        const quantity = watch(`${fieldName}.quantity`);
        const price = watch(`${fieldName}.price`);
        console.log(quantity * price);
        info[i].total = quantity * price;
        // setTotal(info[i].total);
        return (
          <div name={fieldName} key={fieldName}>
            <label>
              Item Name
              <input type="text" name={`${fieldName}.name`} ref={register} />
            </label>

            <label>
              Item Qty
              <input
                type="number"
                name={`${fieldName}.quantity`}
                ref={register}
              />
            </label>

            <label>
              Item Price
              <input type="number" name={`${fieldName}.price`} ref={register} />
            </label>
            <div>
              <h3>Total</h3>
              <h3>{info[i].total || "0.00"}</h3>
            </div>
            <button type="button" onClick={removeItem(i)}>
              Remove
            </button>
          </div>
        );
      })}
      <button type="button" onClick={addItem}>
        Add Item
      </button>
      {/* </form> */}
    </div>
  );
};

export default Itemmm;
