import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useGlobalContext } from "./context";
import "./styles/Invoice.css";
import json from "./data.json";
import iconLeft from "./assets/icon-arrow-left.svg";
import Form from "./Components/Form"

function Invoice() {
  const { chosenId, info, setInfo } = useGlobalContext();
const [showForm, setShowForm] = useState(false)

  useEffect(() => {
    window.scrollTo(0, 0);
    json.forEach((el) => {
      if (el.id === chosenId) {
        setInfo({ ...el });
      }
    });
  }, []);
  const {
    id,
    createdOn,
    paymentDue,
    description,
    paymentTerms,
    clientName,
    clientEmail,
    status,
    senderAddress,
    clientAddress,
    items,
    total,
  } = info;
  console.log(info);
  console.log(info.clientAddress.street);
  return (
    <div className="invoice">
      <Link to="/" className="back">
        <img src={iconLeft} alt="" />
        <h3>Go back</h3>
      </Link>
      <div className="top__component box">
        <div className="flex-horizontal">
          <p>Status</p>
          <div
            style={{ ["marginRight"]: "none" }}
            className={`${
              status === "paid"
                ? "paid status"
                : status === "pending"
                ? "pending status"
                : status === "draft"
                ? "draft status"
                : "status"
            }`}
          >
            <h3>{status || "-"}</h3>
          </div>
        </div>
        <div className="flex-horizontal top__component__btns">
          <button
            className={`${
              status === "paid" ? "invisible" : "top__component__btn edit"
            }`}
          >
            <h3>Edit</h3>
          </button>
          <button className="delete top__component__btn">
            <h3>Delete</h3>
          </button>
          <button
            className={`${
              status === "paid"
                ? "invisible"
                : status === "draft"
                ? "invisible"
                : "top__component__btn markpaid"
            }`}
          >
            <h3>Mark as Paid</h3>
          </button>
        </div>
      </div>

      <div className="bottom__buttons">
        <button
          className={`${
            status === "paid" ? "invisible" : "top__component__btn edit"
          }`}
        >
          <h3>Edit</h3>
        </button>
        <button className="delete top__component__btn">
          <h3>Delete</h3>
        </button>
        <button
          className={`${
            status === "paid"
              ? "invisible"
              : status === "draft"
              ? "invisible"
              : "top__component__btn markpaid"
          }`}
        >
          <h3>Mark as Paid</h3>
        </button>
      </div>

      <div className="bottom__component box">
        <section className="top">
          <div className="id-description">
            <h2>{id || "-"}</h2>
            <p>{description || "-"}</p>
          </div>

          <div className="sender--address">
            <p>{senderAddress.street || "-"}</p>
            <p>{senderAddress.city || "-"}</p>
            <p>{senderAddress.postCode || "-"}</p>
            <p>{senderAddress.country || "-"}</p>
          </div>
        </section>

        <section className="bottom">
          <div className="dates">
            <div>
              <p className="description">Invoice Date</p>
              <h2>
                {new Date(createdOn).toDateString() == "Invalid Date"
                  ? "-"
                  : new Date(createdOn).toDateString().slice(4)}
              </h2>
            </div>
            <div>
              <p className="description">Payment Due</p>
              <h2>
                {new Date(paymentDue).toDateString() == "Invalid Date"
                  ? "-"
                  : new Date(paymentDue).toDateString().slice(4)}
              </h2>
            </div>
          </div>

          <div className="client--address">
            <p className="description">Bill To</p>
            <h2 className="description">{clientName || "-"}</h2>
            <p>{clientAddress.street || "-"}</p>
            <p>{clientAddress.city || "-"}</p>
            <p>{clientAddress.postCode || "-"}</p>
            <p>{clientAddress.country || "-"}</p>
          </div>
          <div className="sender">
            <p className="description">Sent To</p>
            <h2>{clientEmail || "-"}</h2>
          </div>
        </section>

        <section className="calculations">
          {items.map((el) => {
            return (
              <div className="calculations--top">
                <div className="calculations--top__amount">
                  <h3>{el.name}</h3>
                  <p>{`${el.quantity}x\$${el.price}`}</p>
                </div>
                <div>
                  <h3>${el.total}</h3>
                </div>
              </div>
            );
          }) || <p className="description">No items found</p>}

          <div className="calculations--bottom">
            <p>Total</p>
            <h2>${total || "0.00"}</h2>
          </div>
        </section>
      </div>
    </div>
  );
}

export default Invoice;
