import React, { useState, useContext, useLayoutEffect, useEffect } from "react";
import ddata from "./data.json";

const AppContext = React.createContext();

const AppProvider = ({ children }) => {
  const infoFormat = {
    clientAddress: {
      street: "",
      city: "",
      postCode: "",
      country: "",
    },
    clientEmail: "",
    clientName: "",
    createdOn: "",
    description: "",
    id: "",
    items: [],
    paymentDue: "",
    paymentTerms: "",
    senderAddress: {
      street: "",
      city: "",
      postCode: "",
      country: "",
    },
    status: "",
    total: "",
  };

  const [chosenId, setChosenId] = useState([]);
  const [info, setInfo] = useState({ ...infoFormat });
  const [changedInfo, setChangedInfo] = useState({ ...infoFormat });
  const [idx, setIdx] = useState();
  
  const [statuses, setStatuses] = useState({
    draft: false,
    pending: false,
    paid: false,
  });
  const [confDelete, setConfDelete] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [edit, setEdit] = useState(false);

  function useWindowSize() {
    const [size, setSize] = useState([0, 0]);
    useLayoutEffect(() => {
      function updateSize() {
        setSize([window.innerWidth, window.innerHeight]);
      }
      window.addEventListener("resize", updateSize);
      updateSize();
      return () => window.removeEventListener("resize", updateSize);
    }, []);
    return size;
  }

  const [data, setData] = useState([...ddata]);

  useEffect(() => {
    const json = localStorage.getItem("testObj");
    console.log(json);
    if (json) {
      setData(JSON.parse(json));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("testObj", JSON.stringify(data));
    if (data.length === 0) {
      setData([...ddata]);
    }
  });

  const handleChange = (e) => {
    setStatuses({
      ...statuses,
      [e.target.value]: !statuses[e.target.value],
    });
  };

  return (
    <AppContext.Provider
      value={{
        data,
        setData,
        useWindowSize,
        setChosenId,
        chosenId,
        info,
        setInfo,
       
        statuses,
        setStatuses,
        handleChange,
        infoFormat,
        idx,
        setIdx,
        confDelete,
        setConfDelete,
        showForm,
        setShowForm,
        changedInfo,
        setChangedInfo,
        edit,
        setEdit,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};

export const useGlobalContext = () => {
  return useContext(AppContext);
};

export { AppContext, AppProvider };
