import React, { useState } from "react";

const Itemmm = ({ register, info, watch, errors }) => {
  const [item, setItem] = useState([...info.items]);
  const [counter, setCounter] = useState();

  const addItem = () => {
    setItem((prevItem) => [...prevItem, counter]);
    setCounter((prevCounter) => prevCounter + 1);
  };

  const removeItem = (index) => () => {
    setItem((prevItem) => [...prevItem.filter((el, i) => i !== index)]);
    setCounter((prevCounter) => prevCounter - 1);
  };

  return (
    <div>
      {/* <form onSubmit={handleSubmit(onSubmit)}> */}
      {item.map((el, i) => {
        const fieldName = `items[${i}]`;
        const quantity = watch(`${fieldName}.quantity`);
        const price = watch(`${fieldName}.price`);
        // info.items[i].total = quantity * price;
        // const total = [];
        // total.concat(info.items[i].total);
        // console.log(total);
        return (
          <div name={fieldName} key={fieldName}>
            <label>
              Item Name
              <input
                // style={{
                //   border: `${errors?.fieldName?.name}` ? "1px solid red" : "",
                // }}
                type="text"
                name={`${fieldName}.name`}
                ref={register({ required: true, maxLength: 80 })}
              />
            </label>

            <label>
              Item Qty
              <input
                type="number"
                name={`${fieldName}.quantity`}
                ref={register({ required: true, maxLength: 5 })}
              />
            </label>

            <label>
              Item Price
              <input
                type="number"
                name={`${fieldName}.price`}
                ref={register({ required: true, maxLength: 8 })}
              />
            </label>
            <div>
              <h3>Total</h3>
              <h3>{quantity * price || "0.00"}</h3>
            </div>
            <button
              type="button"
              onClick={
                // item.length > 1 ? removeItem(i) : undefined
                an
              }
            >
              Remove
            </button>
          </div>
        );
      })}
      <button type="button" onClick={addItem}>
        Add Item
      </button>
    </div>
  );
};

export default Itemmm;
