import React, { useState } from "react";
import { useGlobalContext } from "./context";
import { Link } from "react-router-dom";
import "./styles/Home.css";
import arrowRight from "./assets/icon-arrow-right.svg";
import iconPlus from "./assets/icon-plus.svg";
import arrowDown from "./assets/icon-arrow-down.svg";
import Filter from "./Filter";

function Home() {
  const {
    data,
    dataJson,
    useWindowSize,
    setChosenId,
    singleInvoice,
  } = useGlobalContext();
  const [innerWidth, innerHeight] = useWindowSize();
  const [showFilter, setShowFilter] = useState(false);
  const [statuses, setStatuses] = useState({
    draft: false,
    pending: false,
    paid: false,
  });

  const handleChange = (e) => {
    setStatuses({
      ...statuses,
      [e.target.value]: !statuses[e.target.value],
    });
    console.log(statuses);
  };

  return (
    <>
      <div className="info__component flex-vertical">
        <div className="flex-horizontal width home__top">
          <div>
            <h1>Invoices</h1>
            <p>{data.length} invoices</p>
          </div>

          <div
            className={`${
              showFilter ? "invisible" : "flex-horizontal home__top__filter"
            }`}
          >
            <p>Filter</p>
            <div className={`${showFilter ? "invisible" : "filter"}`}>
              <div>
                <input
                  type="checkbox"
                  name="Draft"
                  value="draft"
                  onChange={(e) => {
                    handleChange(e);
                  }}
                />
                <label htmlFor="Draft"> Draft</label>
              </div>
              <div>
                <input
                  type="checkbox"
                  name="Pending"
                  value="pending"
                  onChange={(e) => {
                    handleChange(e);
                  }}
                />
                <label htmlFor="checkbox"> Pending</label>
              </div>

              <div>
                <input
                  type="checkbox"
                  name="Paid"
                  value="paid"
                  onChange={(e) => {
                    handleChange(e);
                  }}
                />
                <label htmlFor="Paid"> Paid</label>
              </div>
            </div>
            <button className="home__top__filter__btn">
              <img
                src={iconPlus}
                alt=""
                className="home__top__filter__btn__iconplus"
              />
              <h3>New</h3>
            </button>
          </div>
        </div>

        {data.map((el) => {
          const { id, paymentDue, clientName, total, status } = el;
          if (
            statuses[status] === true ||
            (statuses.draft === statuses.paid &&
              statuses.paid === statuses.pending &&
              statuses.draft === statuses.pending)
          ) {
            return (
              <Link
                onClick={() => {
                  setChosenId(id);
                }}
                to={`/invoice/${id}`}
                style={{ textDecoration: "none" }}
                className="flex-horizontal width list"
              >
                <div className="info__component--section1">
                  <li>
                    <h3>{id || "-"}</h3>
                  </li>
                  <li>
                    {new Date(paymentDue).toDateString() == "Invalid Date"
                      ? "-"
                      : new Date(paymentDue).toDateString().slice(4)}
                  </li>
                  <li>{clientName || "-"}</li>
                </div>
                <div className="info__component--section2">
                  <li>
                    <h2>${total || "0.00"}</h2>
                  </li>
                  <li>{status || "-"}</li>
                  <img src={arrowRight} alt="" />
                </div>
              </Link>
            );
          }
        })}
      </div>
    </>
  );
}

export default Home;
