import React, { useState } from "react";
import { useForm } from "react-hook-form";

const Itemmm = ({ onSubmit }) => {
  const [indexes, setIndexes] = React.useState([]);
  const [counter, setCounter] = React.useState(0);
  const { register, handleSubmit } = useForm();

  //   const onSubmit = (data) => {
  //     console.log(data);
  //   };

  const addFriend = () => {
    setIndexes((prevIndexes) => [...prevIndexes, counter]);
    setCounter((prevCounter) => prevCounter + 1);
  };

  const removeFriend = (index) => () => {
    setIndexes((prevIndexes) => [
      ...prevIndexes.filter((item) => item !== index),
    ]);
    setCounter((prevCounter) => prevCounter - 1);
  };

  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        {indexes.map((index) => {
          const fieldName = `items[${index}]`;
          return (
            <fieldset name={fieldName} key={fieldName}>
              <label>
                Item Name
                <input type="text" name={`${fieldName}.name`} ref={register} />
              </label>
              <label>
                Item Qty
                <input
                  type="text"
                  name={`${fieldName}.quantity`}
                  ref={register}
                />
              </label>

              <label>
                Item Price
                <input type="text" name={`${fieldName}.price`} ref={register} />
              </label>

              <label>
                Item Name
                <input type="text" name={`${fieldName}.name`} ref={register} />
              </label>
              <div>
                <h3>Total</h3>
              </div>

              <button type="button" onClick={removeFriend(index)}>
                Remove
              </button>
            </fieldset>
          );
        })}
        <button type="button" onClick={addFriend}>
          Add Item
        </button>
      </form>
    </div>
  );
};

export default Itemmm;
