import React from "react";
import ReactDom from "react-dom";
import "../styles/Delete.css";
import { Link } from "react-router-dom";
import { useGlobalContext } from "../context";

export default function Delete() {
  const { data, idx, info, confDelete, setConfDelete } = useGlobalContext();

  if (!confDelete) {
    return null;
  }
  return ReactDom.createPortal(
    <>
      <div
        className="darken-bg"
        onClick={() => {
          setConfDelete(false);
        }}
      >
      <div className="delete__component">
        <h1>Confirm Deletion</h1>
        <h3>
          {`Are you sure you want to delete invoice #${info.id}? This action cannot be
        undone.`}
        </h3>
        <div className="delete__component__btns">
          <button
            className="edit top__component__btn"
            onClick={() => {
              setConfDelete(false);
            }}
          >
            <h3>Cancel</h3>
          </button>
          <Link to="/">
            <button
              className="delete top__component__btn"
              onClick={() => {
                data.splice(idx, 1);
              }}
            >
              <h3>Delete</h3>
            </button>
          </Link>
        </div>
      </div></div>
    </>,
    document.getElementById("portal")
  );
}
