import React from "react";
import "../styles/FormButtons.css";
import { useGlobalContext } from "../context";

export const FormButtons = () => {
  const {
    idx,
    edit,
    handleSubmit,
    handleCancel,
    setShowForm,
    setEdit,
    changedInfo,
    setChangedInfo,
  } = useGlobalContext();

  return (
    <div className="form__buttons">
      {edit ? (
        <div className="edit__buttons">
          <button
            className="btn edit"
            onClick={() => {
              setEdit(false);
              handleCancel();
            }}
          >
            Cancel
          </button>

          <button
            className="btn markpaid"
            onClick={(e) => {
              setShowForm(false);
              handleSubmit(e);
            }}
          >
            Save Changes
          </button>
        </div>
      ) : (
        <div className="submit__buttons">
          <button
            className="btn edit"
            onClick={() => {
              setEdit(false);
              handleCancel();
            }}
          >
            Discard
          </button>
          <div>
            <button
              className="btn edit"
              onClick={(e) => {
                setShowForm(false);
                setEdit(false);
                handleSubmit(e);
                setCInfo({ ...Info, status: "draft" });
              }}
            >
              Save as Draft
            </button>

            <button
              className="btn markpaid "
              onClick={(e) => {
                setShowForm(false);
                handleSubmit(e);
                setChangedInfo({
                  ...changedInfo,
                  status: "pending",
                });
              }}
            >
              Save & Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default FormButtons;
