import React, { useState } from "react";
import { useGlobalContext } from "./context";
import { Link } from "react-router-dom";
import "./styles/Home.css";
import arrowRight from "./assets/icon-arrow-right.svg";
import iconPlus from "./assets/icon-plus.svg";
import arrowDown from "./assets/icon-arrow-down.svg";
import Filter from "./Filter";

function Home() {
  const {
    data,
    dataJson,
    useWindowSize,
    setChosenId,
    singleInvoice,
  } = useGlobalContext();
  const [innerWidth, innerHeight] = useWindowSize();
  const [statuses, setStatuses] = useState({
    draft: "",
    pending: "",
    paid: "",
  });
  console.log(dataJson);
  console.log(innerWidth);

  const handleChange = (e) => {
    setStatuses({
      ...statuses,
      [e.target.value]: !statuses[e.target.value],
    });
    console.log(statuses);
  };

  return (
    <>
      <div className="info__component flex-vertical">
        <div className="flex-horizontal width">
          <div>
            <h1>Invoices</h1>
            <p>{data.length} invoices</p>
          </div>

          <div className="flex-horizontal">
            <p>Filter</p>

            <button className="purple__btn">
              <img src={iconPlus} alt="" className="iconplus" />
              New
            </button>
            {/* <Filter /> */}

            <div className="filter">
              <input
                type="checkbox"
                name="Draft"
                value="draft"
                onChange={(e) => {
                  handleChange(e);
                }}
              />
              <label htmlFor="Draft"> Draft</label>
              <input
                type="checkbox"
                name="Pending"
                value="pending"
                onChange={(e) => {
                  handleChange(e);
                }}
              />
              <label htmlFor="checkbox"> Pending</label>
              <input
                type="checkbox"
                name="Paid"
                value="paid"
                onChange={(e) => {
                  handleChange(e);
                }}
              />
              <label htmlFor="Paid"> Paid</label>
            </div>
          </div>
        </div>

        {/* {for (const status in statuses) {
      if (statuses[status] == false) {
        data.map((el) => {
            if (el.status !== `${[status]}`) {
              console.log(el);
              return el;
            }
          })
        );
      }
    }} */}

        {
          data.map((el) => {
            const { id, paymentDue, clientName, total, status } = el;
            if (statuses[status] === true) {
              return (
                <Link
                  onClick={() => {
                    setChosenId(id);
                  }}
                  to={`/invoice/${id}`}
                  style={{ textDecoration: "none" }}
                  className="flex-horizontal width list"
                >
                  <div className="info__component--section1">
                    <li>
                      <h3>{id || "-"}</h3>
                    </li>
                    <li>
                      {new Date(paymentDue).toDateString() == "Invalid Date"
                        ? "-"
                        : new Date(paymentDue).toDateString().slice(4)}
                    </li>
                    <li>{clientName || "-"}</li>
                  </div>
                  <div className="info__component--section2">
                    <li>
                      <h2>${total || "0.00"}</h2>
                    </li>
                    <li>{status || "-"}</li>
                    <img src={arrowRight} alt="" />
                  </div>
                </Link>
              );
            }
          })
        }
      </div>
    </>
  );
}

export default Home;
