import React, { useState, useEffect } from "react";
import { useGlobalContext } from "../context";
import "../styles/Form.css";
import json from "../data.json";

function Form() {
  const { info, setInfo } = useGlobalContext();

  const [create, Setcreate] = useState(false);
  const [edit, setEdit] = useState(false);

  useEffect(() => {
    const chosenId = "RT3080";
    window.scrollTo(0, 0);
    json.forEach((el) => {
      if (el.id === chosenId) {
        setInfo({ ...el });
      }
    });
  }, []);

  const {
    id,
    createdOn,
    paymentDue,
    description,
    paymentTerms,
    clientName,
    clientEmail,
    status,
    senderAddress,
    clientAddress,
    items,
    total,
  } = info;

  const handleChange = (e) => {
    const { name, value, id } = e.target;

    if (!value.trim()) {
      e.target.classList.add("empty");
    }
    if (id) {
      setInfo({ ...info, [id]: { ...info[id], [name]: value } });
    } else {
      setInfo({ ...info, [name]: value });
    }

    console.log(info);
  };

  return (
    <div className>
      <h1>{edit ? `Edit #${info.id}` : "New Invoice"}</h1>
      <h3>Bill From</h3>
      <form action="submit">
        <label htmlFor="senderAddressStreet">Street Address</label>
        <input
          type="text"
          id="senderAddress"
          name="street"
          value={senderAddress.street}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="senderAddressCity">City</label>
        <input
          type="text"
          id="senderAddress"
          name="city"
          value={senderAddress.city}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="senderAddressPostCode">Post Code</label>
        <input
          type="text"
          id="senderAddress"
          name="postCode"
          value={senderAddress.postCode}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="senderAddressCountry">Country</label>
        <input
          type="text"
          id="senderAddress"
          name="country"
          value={senderAddress.country}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <h3>Bill To</h3>
        <label htmlFor="clientName">Client's Name</label>
        <input
          type="text"
          name="clientName"
          value={clientName}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="clientEmail">Client's Email</label>
        <input
          type="email"
          name="clientEmail"
          value={clientEmail}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="clientAddressStreet">Street Address</label>
        <input
          type="text"
          id="clientAddress"
          name="street"
          value={clientAddress.street}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="clientAddressCity">City</label>
        <input
          type="text"
          id="clientAddress"
          name="city"
          value={clientAddress.city}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="clientAddressPostCode">Post Code</label>
        <input
          type="text"
          id="clientAddress"
          name="postCode"
          value={clientAddress.postCode}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="clientAddressCountry">Country</label>
        <input
          type="text"
          id="clientAddress"
          name="country"
          value={clientAddress.country}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="paymentDue">Invoice Date</label>
        <input
          type="date"
          name="paymentDue"
          value={paymentDue}
          onChange={(e) => {
            handleChange(e);
          }}
        />
        <label htmlFor="paymentTerms">Payment Terms</label>
        <select
          className=""
          name="paymentTerms"
          value={paymentTerms}
          onChange={(e) => {
            handleChange(e);
          }}
        >
          <option value="1">Net 1 Day</option>;
          <option value="7">Net 7 Day</option>;
          <option value="14">Net 14 Day</option>;
          <option value="30">Net 30 Day</option>;
        </select>
        <label htmlFor="description">Project Description</label>
        <input
          type="text"
          name="description"
          value={description}
          onChange={(e) => {
            handleChange(e);
          }}
        />
      </form>
    </div>
  );
}

export default Form;
