import React from "react";
import { useGlobalContext } from "../context";
import { useForm } from "react-hook-form";
import ReactDOM from "react-dom";


function Test() {
  const [indexes, setIndexes] = React.useState([]);
  const [counter, setCounter] = React.useState(0);
    const { register, handleSubmit } = useForm();
//   const { info, setInfo } = useGlobalContext();

  const onSubmit = (data) => {
    console.log(data);
  };

  const addFriend = () => {
    setIndexes((prevIndexes) => [...prevIndexes, counter]);
    setCounter((prevCounter) => prevCounter + 1);
  };

  const removeFriend = (index) => () => {
    setIndexes((prevIndexes) => [
      ...prevIndexes.filter((item) => item !== index),
    ]);
    setCounter((prevCounter) => prevCounter - 1);
  };

  const clearFriends = () => {
    setIndexes([]);
  };

  console.log(counter, indexes);

const he = [{clientAddress: {street: "84 Church Way", city: "Bradford", postCode: "BD1 9PB", country: "United Kingdom"},
clientEmail: "alexgrim@mail.com",
clientName: "Alex Grim",
createdOn: "2021-08-21",
description: "Graphic Design",
id: "XM9141",
items:  [{name: "Banner Design", quantity: "1", price: "156", total: 156},
{name: "Email Design", quantity: "2", price: "199", total: 398}],
paymentDue: "2021-09-20",
paymentTerms: "30",
senderAddress: {street: "19 Union Terrace", city: "London", postCode: "E1 3EZ", country: "United Kingdom"},
status: "pending",
total: 554}]

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      {indexes.map((index) => {
        const fieldName = `friends[${index}]`;
        return (
          <fieldset name={fieldName} key={fieldName}>
            <label>
              First Name {index}:
              <input
                type="text"
                name={`${fieldName}.firstName`}
                ref={register}
              />
            </label>

            <label>
              Last Name {index}:
              <input
                type="text"
                name={`${fieldName}.lastName`}
                ref={register}
              />
            </label>
            <button type="button" onClick={removeFriend(index)}>
              Remove
            </button>
          </fieldset>
        );
      })}

      <button type="button" onClick={addFriend}>
        Add Friend
      </button>
      <button type="button" onClick={clearFriends}>
        Clear Friends
      </button>
      <input type="submit" />
    </form>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<Test />, rootElement);

export default Test;
